import React, { useState, useEffect } from 'react'
import { Table } from 'reactstrap'

const BlogList = () => {
  const [pages, setPages] = useState([])
  const [articles, setArticles] = useState([])
  const [authors, setAuthors] = useState({}) // Yazarları saklamak için bir nesne oluşturun

  useEffect(() => {
    LoadPages()
    LoadArticles()
  }, [])

  async function LoadPages () {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/pages')
      if (!response.ok) {
        throw new Error('Veri çekme hatası')
      }
      const data = await response.json()
      setPages(data)
    } catch (error) {
      console.error('Veri çekerken hata:', error)
    }
  }

  async function LoadArticles () {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/articles')
      if (!response.ok) {
        throw new Error('Veri çekme hatası')
      }
      const data = await response.json()
      setArticles(data)

      // Makaleleri ve yazarlarını eşleştirin
      const authorMap = {}
      data.forEach(article => {
        authorMap[article.id] = article.articleTitle // Burada articleTitle veya yazarın adının olduğu uygun bir alanı kullanmalısınız
      })
      setAuthors(authorMap)
    } catch (error) {
      console.error('Veri çekerken hata:', error)
    }
  }

  return (
    <Table dark>
      <thead>
        <tr>
          <th>Title</th>
          <th>Content</th>
          <th>Article Author</th>
        </tr>
      </thead>
      <tbody>
        {pages.map(page => (
          <tr key={page.id}>
            <td>{page.pageTitle}</td>
            <td>{page.content.slice(0, 10)}</td>
            <td>{authors[page.articleTitleforPage]}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  )
}

export default BlogList
