import React, { useState, useEffect } from 'react'
import { Table, Button } from 'reactstrap'
import PagesList from './PagesList'
import ArticleList from './ArticleList'

const BlogList = () => {
  const [pages, setPages] = useState([])
  const [articles, setArticles] = useState([])

  return (
    <Table dark>
      <thead>
        <tr>
          <th>Title</th>
          <th>Content</th>
          <th>Author</th>
          <th>Image</th>
          <th>Page Title</th>
          <th>Article Title</th>
        </tr>
      </thead>
      <tbody>
        {/* Render the PagesList component */}
        {pages.map(page => (
          <tr key={page.id}>
            <td>{page.pageTitle}</td>
            <td>{page.content}</td>
            <td>{page.author}</td>
            <td>{page.image}</td>
            <td>{page.pageTitle}</td> {/* Assuming this is the page title */}
            <td></td> {/* No article title provided for pages */}
          </tr>
        ))}

        {/* Render the ArticleList component */}
        {articles.map(article => (
          <tr key={article.id}>
            <td>{article.title}</td>
            <td>{article.content}</td>
            <td>{article.author}</td>
            <td>{article.image}</td>
            <td></td> {/* No page title provided for articles */}
            <td>{article.articleTitle}</td>{' '}
            {/* Assuming this is the article title */}
          </tr>
        ))}
      </tbody>
    </Table>
  )
}

export default BlogList
